import React, { Component } from "react";
import { MDBBtn, MDBCol, MDBContainer, MDBRow } from "mdbreact";
import "./index.css";
import Navbar from './components/NavBar';
import About from "./components/About";
import Toolkit from "./components/ToolKit";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Footer from "./components/Footer";


class App extends Component {
  constructor(){
    super()
    this.state = {
      active:0,
      sections:[]
    }  
    this.scrollSpyText = React.createRef();
  }


  componentDidMount(){
    let sections = this.scrollSpyText.current.getElementsByTagName("h4");
    this.setState({ sections })
  }

  handleScroll = e =>{
    const scrollTop = e.target.scrollTop;
    const sections = this.state.sections;
    const lastIndex = sections.length - 1;

    for (let i = 0; i < lastIndex; i++) {
      if ((scrollTop> sections[i].offsetTop - 20) && (scrollTop < sections[i +1].offsetTop - 20  )){
        this.setState({ active : i }); } }; if (scrollTop> sections[lastIndex].offsetTop - 20)
        this.setState({active: lastIndex});
      }

  render() {
    return (
      <div>
        <Navbar onScroll={this.handleScroll} />
        <About />
        <Toolkit />
        <Projects />
        <Contact />
        <Footer />
        </div>
    );
  }
}

export default App;
