import React, { Component } from "react";
import {
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarNav,
  MDBNavItem,
  MDBNavLink,
  MDBNavbarToggler,
  MDBCollapse,
  MDBFormInline,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBScrollspyBox,
  MDBScrollspyList,
  MDBScrollspyListItem,
  MDBScrollspyText
} from "mdbreact";
import { BrowserRouter as Router } from "react-router-dom";

class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      active: 0,
      sections: []
    };

    this.scrollSpyText = React.createRef();
  }

  componentDidMount() {
    let sections = this.scrollSpyText.current.getElementsByTagName("h4");
    this.setState({ sections });
  }

  handleScroll = e => {
    const scrollTop = e.target.scrollTop;
    const sections = this.state.sections;
    const lastIndex = sections.length - 1;

    for (let i = 0; i < lastIndex; i++) {
      if (
        scrollTop > sections[i].offsetTop - 20 &&
        scrollTop < sections[i + 1].offsetTop - 20
      ) {
        this.setState({ active: i });
      }
    }
    if (scrollTop > sections[lastIndex].offsetTop - 20)
      this.setState({ active: lastIndex });
  };

  toggleCollapse = () => {
    this.setState({ isOpen: !this.state.isOpen });
  };

  render() {
    return (
      <Router>
        <MDBScrollspyBox>
          <MDBScrollspyList color="grey" className="d-flex justify-content-end">
            <MDBScrollspyListItem
              href="#section1"
              active={this.state.active === 0}
            >
              section1
            </MDBScrollspyListItem>
            <MDBScrollspyListItem
              href="#section2"
              active={this.state.active === 1}
            >
              section2
            </MDBScrollspyListItem>
            <MDBScrollspyListItem
              href="#section3"
              active={this.state.active === 2}
            >
              section3
            </MDBScrollspyListItem>
          </MDBScrollspyList>

          <MDBNavbar color="indigo" dark expand="md">
            <MDBNavbarBrand>
              <strong className="white-text">Navbar</strong>
            </MDBNavbarBrand>
            <MDBNavbarToggler onClick={this.toggleCollapse} />
            <MDBCollapse id="navbarCollapse3" isOpen={this.state.isOpen} navbar>
              <MDBNavbarNav left>
                <MDBNavItem active>
                  <MDBNavLink to="#!">Home</MDBNavLink>
                </MDBNavItem>
                <MDBNavItem>
                  <MDBNavLink to="#!">Features</MDBNavLink>
                </MDBNavItem>
                <MDBNavItem>
                  <MDBNavLink to="#!">Pricing</MDBNavLink>
                </MDBNavItem>
                <MDBNavItem>

                </MDBNavItem>
              </MDBNavbarNav>
              <MDBNavbarNav right>
                <MDBNavItem>
                  <MDBFormInline waves>
                    <div className="md-form my-0">
                      <input
                        className="form-control mr-sm-2"
                        type="text"
                        placeholder="Search"
                        aria-label="Search"
                      />
                    </div>
                  </MDBFormInline>
                </MDBNavItem>
              </MDBNavbarNav>
            </MDBCollapse>
          </MDBNavbar>
        </MDBScrollspyBox>
      </Router>
    );
  }
}

export default Navbar;
